Page.LayScript:

if(event == 'editcode'){
  ZW.CMS.TabAdd("编辑[" + data.Name + "]", Path +'Code' + Querys +(Querys == '?' ? '' : '&') +"TypeName="+ encodeURIComponent(data.ID));
}else if(event == 'pagelist'){
  ZW.CMS.TabAdd("页面列表[" + data.Name + "]", Path +'Detail' + Querys +(Querys == '?' ? '' : '&') +"TypeName="+ encodeURIComponent(data.ID));
}


if(event == 'pagedetail'){
  ZW.CMS.TabAdd("页面详情[" + data.Name + "]", Path +'PageDetail' + Querys +(Querys == '?' ? '' : '&') +"PageName="+ data.Name);
}else if(event == 'editcode'){
  ZW.CMS.TabAdd("编辑[" + data.Name + "]", Path + 'PageCode' + Querys +(Querys == '?' ? '' : '&') +"PageName="+ data.Name);
}else if(event == 'editpage'){
  layer.open({
              title: '更新数据',
              type: 2,
              area: [ZW.CMS.Common.IsMobilePhone() ? '100%' : '600px', '428px'],
              fixed: true,
              maxmin: true,
              scroll: false,
              content: 'PageEdit' + Querys + (Querys == '?' ? '' : '&') +'PageName=' + data.Name + "&iframe=1"
          });
}else if(event == 'pageadd'){
  layer.open({
              title: '添加数据',
              type: 2,
              area: [ZW.CMS.Common.IsMobilePhone() ? '100%' : '600px', '428px'],
              fixed: true,
              maxmin: true,
              scroll: false,
              content: 'PageEdit' + Querys + (Querys == '?' ? '' : '&') +"iframe=1"
          });
}else if(event == 'control'){
  ZW.CMS.TabAdd("可视化控件[" + data.Name + "]", Path + 'PageControl' + Querys +(Querys == '?' ? '' : '&') +"PageName="+ data.Name);
}

Page.Script:

var changeValue = function(e){
  var $ = layui.$;
  if(e.elem.checked){
    $("#PageSize").parents('.layui-form-item').removeClass('zw-dpn');
  }else{
    $("#PageSize").parents('.layui-form-item').addClass('zw-dpn');
  }
}
var ChangeType = function(e){
  var $ = layui.$;
   console.log(e.value);
  if(e.value == 'List'){
    $("#IsToolBar,#IsNum,#IsBox,#IsPage").parents('.layui-form-item').removeClass('zw-dpn');
  }else{
    $("#IsToolBar,#IsNum,#IsBox,#IsPage").parents('.layui-form-item').addClass('zw-dpn');
  }
}

Page.LayScript:

if(event == 'setattr'){
  var url = ZW.CMS.Common.GetUrl(Path, ConfigUrl["setattr"].Url);
  url = url + Querys + (Querys == '?' ? '' : '&') + "iframe=1";
  layer.open({
      title: '设置Tab属性',
      type: 2,
      area: [ZW.CMS.Common.IsMobilePhone() ? '100%' : '617px', '400px'],
      fixed: true,
      maxmin: true,
      scroll: false,
      content: url
  });
}


if(event == 'setwidth'){
  layer.open({
        title: '更新菜单宽',
        type: 2,
        area: [ZW.CMS.Common.IsMobilePhone() ? '100%' : '450px', '200px'],
        fixed: true,
        maxmin: true,
        scroll: false,
        content: '/Admin/PageSet/SetMenuWidth' + Querys + "&iframe=1"
    });
}

Page.Script:

      var FieldTypeChange = function(e){
        SelectChangeValue(e.elem.value);
      }
      var SelectChangeValue = function(val){
        var $ = layui.$;
		console.log(val);
        if(val=='label'){ 
          $('#Placeholder,#Pattern,#MinLength,#MaxLength,#Format,#Encrypt,#ValueFormat,#PassWordType').parents('.layui-form-item').addClass('zw-dpn');
          $('#Data,#Link').parents('.layui-form-item').removeClass('zw-dpn');
        }else if(val=='select' || val=='searchselect'|| val=='selects'|| val=='city'){
          $('#FirstOption,#Data').parents('.layui-form-item').removeClass('zw-dpn');
          $('#Placeholder,#Pattern,#MinLength,#MaxLength,#Format,#Link,#ValueFormat,#PassWordType').parents('.layui-form-item').addClass('zw-dpn');
        }else if(val=='text' || val=='password' || val=='textarea'|| val=='checkcode'){
          $('#Placeholder,#Pattern,#MinLength,#MaxLength,#Format,#ValueFormat').parents('.layui-form-item').removeClass('zw-dpn');
          $('#FirstOption,#Data,#Link,#PassWordType').parents('.layui-form-item').addClass('zw-dpn');
          if(val == 'password')$('#PassWordType').parents('.layui-form-item').removeClass('zw-dpn');
        }else if(val=='editor' || val=='code' || val=='editor' || val=='image'){
          $('#Width,#Height').parents('.layui-form-item').removeClass('zw-dpn');
          $('#FirstOption,#Data,#Link,#MinLength,#MaxLength,#Format,#Placeholder,#ValueFormat,#PassWordType').parents('.layui-form-item').addClass('zw-dpn');
        }else if(val=='hidden'|| val=='checkbox'||val=='checkbox1'||val=='checkbox2' || val=='radio'|| val=='file'|| val=='files' || val=='page' || val=='map' || val=='pointmap'|| val=='groups'){
          $('#FirstOption,#Placeholder,#Pattern,#MinLength,#MaxLength,#Format,#Width,#Height,#Link,#ValueFormat,#PassWordType').parents('.layui-form-item').addClass('zw-dpn');
          $("#Data").parents('.layui-form-item').removeClass('zw-dpn');
        }else if(val=='time' || val=='date'|| val=='dates'||val=='datetime'){
          $('#Placeholder').parents('.layui-form-item').removeClass('zw-dpn');
          $('#FirstOption,#Data,#Pattern,#MinLength,#MaxLength,#Format,#ValueFormat,#PassWordType').parents('.layui-form-item').addClass('zw-dpn');
        }else if(val=='slider' || val=='textslider'){
          $('#Width,#Height').parents('.layui-form-item').removeClass('zw-dpn');
          $('#FirstOption,#Data,#Pattern,#Format,#ValueFormat,#Placeholder,#PassWordType').parents('.layui-form-item').addClass('zw-dpn');
        }else if(val == 'hr'){
          $('#FirstOption,#Data,#Pattern,#MinLength,#MaxLength,#Format,#Link,#ValueFormat,#Placeholder,#PassWordType').parents('.layui-form-item').addClass('zw-dpn');
        }else{
          $('.layui-form-item').removeAttr('zw-dpn');
        }
      }
      var ChangeValue = function(e){
        var $ = layui.$;
        var val = e.delegateTarget.value;
        if(val !='' && $("#LayFilter").val() == '')$("#LayFilter").val(val);
      }
      var ValueFormatBack = function(e){
        var $ = layui.$;
        var val = e.delegateTarget.value;
        if(val.length == 0)return;
        if(/^(Date|Guid|Substring|PadLeft|PadRight):[\s\S]+$/gi.test(val)){

        }else{
          layer.alert('输入的值格式不正确.',{icon:2},function(e){
            $('#ValueFormat').focus();
            layer.close(e);
          });
        }
      }


var FieldTypeChange = function(e){
  SelectChangeValue(e.elem.value);
}
var SelectChangeValue = function(val){
  var $ = layui.$;
  if(val=='label'){ 
    $('#Placeholder,#Pattern,#MinLength,#MaxLength,#Format,#Encrypt').parents('.layui-form-item').addClass('zw-dpn');
    $('#Data,#Link').parents('.layui-form-item').removeClass('zw-dpn');
  }else if(val=='select' || val=='searchselect'|| val=='selects'|| val=='city'){
    $('#FirstOption,#Format,#Data').parents('.layui-form-item').removeClass('zw-dpn');
    $('#Placeholder,#Pattern,#MinLength,#MaxLength,#Link').parents('.layui-form-item').addClass('zw-dpn');
  }else if(val=='text' || val=='password' || val=='textarea'|| val=='checkcode'){
    $('#Placeholder,#Pattern,#MinLength,#MaxLength,#Format').parents('.layui-form-item').removeClass('zw-dpn');
    $('#FirstOption,#Data,#Link').parents('.layui-form-item').addClass('zw-dpn');
  }else if(val=='editor' || val=='code' || val=='editor'){
    $('#Width,#Height').parents('.layui-form-item').removeClass('zw-dpn');
    $('#FirstOption,#Data,#Link,#MinLength,#MaxLength,#Placeholder,#Format').parents('.layui-form-item').addClass('zw-dpn');
  }else if(val=='hidden'|| val=='checkbox'||val=='checkbox1'||val=='checkbox2' || val=='radio'|| val=='file'|| val=='files' || val=='page' || val=='map' || val=='pointmap'|| val=='groups'){
    $('#Format').parents('.layui-form-item').removeClass('zw-dpn');
    $('#FirstOption,#Data,#Placeholder,#Pattern,#MinLength,#MaxLength,#Width,#Height,#Link').parents('.layui-form-item').addClass('zw-dpn');
  }else if(val=='time' || val=='date'|| val=='dates'||val=='datetime'){
    $('#Placeholder,#Format').parents('.layui-form-item').removeClass('zw-dpn');
    $('#FirstOption,#Data,#Pattern,#MinLength,#MaxLength').parents('.layui-form-item').addClass('zw-dpn');
  }else if(val=='slider' || val=='textslider'){
    $('#Width,#Height').parents('.layui-form-item').removeClass('zw-dpn');
    $('#FirstOption,#Data,#Pattern,#Format,#ValueFormat,#Placeholder').parents('.layui-form-item').addClass('zw-dpn');
  }else if(val == 'hr'){
    $('#FirstOption,#Data,#Pattern,#MinLength,#MaxLength,#Format,#Link,#ValueFormat,#Placeholder,#PassWordType').parents('.layui-form-item').addClass('zw-dpn');
  }else{
    $('.layui-form-item').removeAttr('zw-dpn');
  }
}
var ChangeValue = function(e){
  var $ = layui.$;
  var val = e.delegateTarget.value;
  if(val!='' && $("#LayFilter").val()=='')$("#LayFilter").val(val);
}
var ValueFormatBack = function(e){
  var $ = layui.$;
  var val = e.delegateTarget.value;
  if(val.length == 0)return;
  if(/^(Date|Guid|Substring|PadLeft|PadRight):[\s\S]+$/gi.test(val)){

  }else{
    layer.alert('输入的值格式不正确.',{icon:2},function(e){
      $('#ValueFormat').focus();
      layer.close(e);
    });
  }
}


var ChangeValue = function(e){
  var $ = layui.$;
  var val = e.delegateTarget.value;
  if(val != '' && $("#LayFilter").val() == '')$("#LayFilter").val(val);
}
var ChangeType = function(e){
console.log(e);
  var $ = layui.$;
  var val = e.value;
  if(val == 'Open'){
    $('#WindowWidth,#WindowHeight').parents('.layui-form-item').removeClass('zw-dpn');
  }else{
    $('#WindowWidth,#WindowHeight').parents('.layui-form-item').addClass('zw-dpn');
  }
}


var ChangeValue = function(e){
  var $ = layui.$;
  var val = e.delegateTarget.value;
  if(val != '' && $("#LayFilter").val() == '')$("#LayFilter").val(val);
}
var ChangeType = function(e){
console.log(e);
  var $ = layui.$;
  var val = e.value;
  if(val == 'Open'){
    $('#WindowWidth,#WindowHeight').parents('.layui-form-item').removeClass('zw-dpn');
  }else{
